INSERT INTO `lei_daima` VALUES ('1', 'JS替换及过滤多个重复指定字符串', '<p><strong>一、去掉两端逗号</strong></p><pre class=\"brush:js;toolbar:false\">function&nbsp;trim(str){&nbsp;//删除左右两端的逗号\r\n　&nbsp;//去掉第一个逗号\r\n&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(str.substr(0,1)==&#39;,&#39;)&nbsp;str=str.substr(1);\r\n&nbsp;&nbsp;&nbsp;&nbsp;//去掉最后一个逗号\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;reg=/,$/gi;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;str=str.replace(reg,&quot;&quot;);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;str;\r\n}</pre><p><strong>二、替换多个指定的字符串为目标字符串</strong><br/></p><pre class=\"brush:js;toolbar:false\">function&nbsp;deletezd(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;arr=$(&quot;#tel&quot;).val();\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;ch_t&nbsp;=&nbsp;&quot;，&quot;;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;reg_t&nbsp;=&nbsp;&quot;/&quot;+ch_t+&quot;/g&quot;;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;arr&nbsp;=&nbsp;arr.replace(eval(reg_t),&quot;,&quot;);&nbsp;&nbsp;//将多个全角逗号转为半角逗号\r\n}</pre><p><strong>三、去掉字符串组中，重复的字符串</strong><br/></p><pre class=\"brush:js;toolbar:false\">function&nbsp;deleteRepetionChar(){//与第一、二组合应用，并实现重复字符串的过滤\r\n&nbsp;&nbsp;&nbsp;&nbsp;arr=$(&quot;#tel&quot;).val();\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;ch_t&nbsp;=&nbsp;&quot;，&quot;;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;reg_t&nbsp;=&nbsp;&quot;/&quot;+ch_t+&quot;/g&quot;;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;arr&nbsp;=&nbsp;arr.replace(eval(reg_t),&quot;,&quot;);&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;arr=trim(arr);//去掉两端空格&nbsp;&nbsp;trim要与第一个function组合用\r\n&nbsp;&nbsp;&nbsp;&nbsp;//先判断输入进来的是数组对象还是字符串\r\n&nbsp;&nbsp;&nbsp;&nbsp;if(&nbsp;typeof&nbsp;arr&nbsp;==&nbsp;&quot;object&quot;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;arr&nbsp;=&nbsp;arr;\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;else&nbsp;if(&nbsp;typeof&nbsp;arr&nbsp;==&nbsp;&quot;string&quot;){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//字符串转为数组\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;arr&nbsp;=&nbsp;arr.split(&quot;,&quot;)\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;//存放不相同的数据\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;box&nbsp;=&nbsp;[];//存放去重数据\r\n&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;thisArr;//当前的值arr[i]\r\n&nbsp;&nbsp;&nbsp;&nbsp;//遍历数组\r\n&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(var&nbsp;i&nbsp;=&nbsp;0;&nbsp;i&nbsp;&lt;&nbsp;arr.length;&nbsp;i++)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;thisArr&nbsp;=&nbsp;arr[i]\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//定义一个旗子，检查是不是有重复的字符\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;flag&nbsp;=&nbsp;true;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//遍历存放数据盒子，检查是否等于当前元素\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(var&nbsp;j&nbsp;=&nbsp;0;&nbsp;j&nbsp;&lt;&nbsp;box.length;&nbsp;j++)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//如果值存在\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(box[j]==thisArr){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;flag&nbsp;=&nbsp;false;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;//结束for循环\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(flag==false){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//continue返回执行函数而不压入数组\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;continue&nbsp;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;box.push(thisArr);\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;&nbsp;$(&quot;#tel&quot;).val(box);\r\n&nbsp;&nbsp;&nbsp;&nbsp;//alert(box)\r\n&nbsp;&nbsp;&nbsp;&nbsp;//return&nbsp;box\r\n}</pre><p><strong>四、去掉字符串中的多个连续空格为1个</strong></p><pre class=\"brush:js;toolbar:false\">var&nbsp;regEx&nbsp;=&nbsp;/s+/g;\r\n&nbsp;&nbsp;&nbsp;&nbsp;jieguo=jieguo.replace(regEx,&nbsp;&#39;&nbsp;&#39;);</pre><p><br/></p>', '2017-03-01 16:05:56', '1', '1', '10000');
INSERT INTO `lei_daima` VALUES ('2', '链接提示并转向', '<p><strong>为页面上所有链接规定默认目标：</strong></p><pre class=\"brush:html;toolbar:false\">&lt;head&gt;\r\n&lt;base&nbsp;target=&quot;_blank&quot;&nbsp;/&gt;\r\n&lt;/head&gt;\r\n\r\n&lt;body&gt;\r\n&lt;a&nbsp;href=&quot;http://www.w3school.com.cn&quot;&gt;W3School&lt;/a&gt;\r\n&lt;/body&gt;</pre><p><strong>按钮删除前提示，确认后执行操作</strong></p><pre class=\"brush:html;toolbar:false\">&lt;input&nbsp;name=&quot;button&quot;&nbsp;type=&quot;button&quot;&nbsp;onclick=&quot;javascript:if(confirm(&#39;确认删除？&#39;))document.location.href=&#39;show.php?id=123&#39;&quot;&nbsp;value=&quot;删除&quot;&nbsp;/&gt;</pre><p><strong>操作成功并转向</strong></p><pre class=\"brush:php;toolbar:false\">echo&nbsp;&quot;&lt;script&gt;alert(&#39;场次成功！&#39;);location.href=&#39;?&quot;.$back_url.&quot;&#39;;&lt;/script&gt;&quot;;\r\nexit;</pre><p><strong>错误提示并后退</strong><br/></p><pre class=\"brush:php;toolbar:false\">echo&nbsp;&quot;&lt;script&nbsp;language=javascript&gt;alert(&#39;不能为空！&#39;);javascript:history.back(-1);&lt;/script&gt;&quot;;\r\nexit;</pre><p><br/></p>', '2017-03-01 16:06:38', '1', '1', '11000');
INSERT INTO `lei_daima` VALUES ('3', '弹出层中按钮与激活按钮冲突的解决（防冒泡）', '<pre class=\"brush:js;toolbar:false\">$(document).click(function(){\r\n	$(&quot;#flpm_box&quot;).hide();//点击任何位置隐藏层\r\n});\r\n	\r\n$(&quot;#flpm_box&quot;).click(function(event){\r\n	event.stopPropagation();//排除弹出层本身\r\n});\r\n	\r\n$(&quot;#pid_title&quot;).click(function(event){\r\n	event.stopPropagation();//排除激活按钮\r\n});\r\n\r\n//除此之外，要在弹出层中的选项，加上点击赋值和隐藏flpm_box弹出层的事件，就完美解决！</pre><p><br/></p>', '2017-03-01 16:06:56', '1', '1', '100000');
INSERT INTO `lei_daima` VALUES ('4', '验证输入是否符合要求及替换', '<p><strong>验证手机号是否符合标准</strong></p><pre class=\"brush:js;toolbar:false\">var&nbsp;partten&nbsp;=&nbsp;/^1[3,5,7,8]\\d{9}$/;&nbsp;\r\n\r\nif(partten.test(thisArr)){//判断是否符合手机号码格式\r\n&nbsp;&nbsp;&nbsp;&nbsp;//标准\r\n}else{\r\n&nbsp;&nbsp;&nbsp;&nbsp;//不标准\r\n}</pre><p><strong>限制输入只能英文或数字</strong></p><pre class=\"brush:js;toolbar:false\">//允许输入数字点，replace(/[^\\w\\.\\/]/ig,&#39;&#39;)&nbsp;&nbsp;&nbsp;\r\n//限制只能数字&nbsp;replace(/\\D/g,&#39;&#39;)\r\n\r\nonkeyup=&quot;this.value=this.value.replace(/[^\\w\\/]/ig,&#39;&#39;);this.value=this.value.toUpperCase()&quot;&nbsp;&nbsp;\r\nonafterpaste=&quot;this.value=this.value.replace(/[^\\w\\/]/ig,&#39;&#39;);this.value=this.value.toUpperCase()&quot;&nbsp;\r\nonblur=&quot;this.value=this.value.replace(/[^\\w\\/]/ig,&#39;&#39;);this.value=this.value.toUpperCase()&quot;</pre><p><br/></p>', '2017-03-01 16:07:07', '1', '1', '10000');
INSERT INTO `lei_daima` VALUES ('5', 'JQ获取及选中radio及select的值', '<p><strong>JQ获取radio的值</strong></p><pre class=\"brush:js;toolbar:false\">huifu=$(&quot;input[name=&#39;huifu&#39;]:checked&quot;).val();</pre><p><strong>JQ获取select值</strong></p><pre class=\"brush:xml;toolbar:false\">pass=$(&quot;#pass&nbsp;option:selected&quot;).val();</pre><p><strong>JQ获取checkbox值</strong></p><pre class=\"brush:js;toolbar:false\">pass=$(&#39;#tel_sms:checked&#39;).val()</pre><p><strong>JQ给radio或checkbox指定值选中</strong></p><pre class=\"brush:js;toolbar:false\">//radio\r\n$(&quot;:radio[class=&#39;bk_1_&quot;+cnum+&quot;&#39;][value=&#39;&quot;&nbsp;+&nbsp;cxuan&nbsp;+&nbsp;&quot;&#39;]&quot;).prop(&quot;checked&quot;,&nbsp;&quot;checked&quot;);\r\n\r\n//checkbox\r\nif($(&quot;:checkbox[class=&#39;bk_2_&quot;+xuhao+&quot;&#39;][value=&#39;&quot;&nbsp;+&nbsp;cxuan&nbsp;+&nbsp;&quot;&#39;]&quot;).prop(&quot;checked&quot;)){\r\n　　$(&quot;:checkbox[class=&#39;bk_2_&quot;+xuhao+&quot;&#39;][value=&#39;&quot;&nbsp;+&nbsp;cxuan&nbsp;+&nbsp;&quot;&#39;]&quot;).prop(&quot;checked&quot;,false);\r\n}else{\r\n　　$(&quot;:checkbox[class=&#39;bk_2_&quot;+xuhao+&quot;&#39;][value=&#39;&quot;&nbsp;+&nbsp;cxuan&nbsp;+&nbsp;&quot;&#39;]&quot;).prop(&quot;checked&quot;,true);\r\n}</pre><p><br/></p>', '2017-03-01 16:07:16', '1', '1', '10000');
INSERT INTO `lei_daima` VALUES ('6', '通过JQ让元素闪动的效果', '<p><strong>方式一：</strong><br/></p><pre class=\"brush:js;toolbar:false\">&lt;script&nbsp;type=&quot;text/javascript&quot;&nbsp;&gt;\r\n&nbsp;&nbsp;function&nbsp;blink(selector){\r\n&nbsp;&nbsp;&nbsp;&nbsp;$(selector).fadeOut(&#39;slow&#39;,&nbsp;function(){//速度slow,normal,fast\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$(this).fadeIn(&#39;slow&#39;,&nbsp;function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;blink(this);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;});\r\n&nbsp;&nbsp;&nbsp;&nbsp;});\r\n&nbsp;&nbsp;}\r\n&nbsp;&nbsp;$(document).ready(function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;blink(&#39;.blink&#39;);\r\n&nbsp;&nbsp;});&nbsp;\r\n&lt;/script&gt;\r\n\r\n&lt;span&nbsp;class=&quot;blink&quot;&nbsp;&gt;点击进入&lt;/span&gt;</pre><p><strong>方式二：</strong><br/></p><pre class=\"brush:js;toolbar:false\">$(&quot;.blink&quot;).fadeOut(300).fadeIn(300).fadeOut(300).fadeIn(300)</pre><p><br/></p>', '2017-03-01 16:07:25', '1', '1', '15000');
INSERT INTO `lei_daima` VALUES ('7', 'JS对返回字符串转的处理', '<p>1、JQ分割字符串为数组：</p><pre class=\"brush:js;toolbar:false\">var&nbsp;give_strs=&nbsp;new&nbsp;Array();&nbsp;//定义一数组\r\ngive_strs=result.split(&quot;|&quot;);&nbsp;//字符分割&nbsp;\r\nif&nbsp;(give_strs[0]==&quot;0&quot;){\r\n}</pre><p>2、JS判断是否存在字符串：<br/></p><pre class=\"brush:js;toolbar:false\">if&nbsp;(result.indexOf(&quot;正确&quot;)&nbsp;&gt;=&nbsp;0){\r\n&nbsp;&nbsp;&nbsp;&nbsp;$(&quot;#nowtel&quot;).html(&#39;111&#39;);\r\n}</pre><p><br/></p>', '2017-03-01 16:07:36', '1', '1', '14000');
INSERT INTO `lei_daima` VALUES ('8', 'CSS及表单常用代码', '<p><strong>圆角CSS：</strong></p><pre class=\"brush:css;toolbar:false\">-moz-border-radius:2px;-webkit-border-radius:2px;border-radius:2px;</pre><p><strong>强制换行：</strong></p><pre class=\"brush:css;toolbar:false\">white-space:pre-wrap;word-wrap:break-all;</pre><p><strong>输入提示、隐藏焦点、禁用默认填充：</strong><br/></p><pre class=\"brush:html;toolbar:false\">&lt;input&nbsp;type=&quot;text&quot;&nbsp;value=&quot;&quot;&nbsp;placeholder=&quot;请输入短信验证码&quot;&nbsp;hidefocus=&quot;true&quot;&nbsp;autocomplete=&quot;off&quot;&nbsp;/&gt;</pre><p><strong>上传文件表单属性：</strong></p><pre class=\"brush:html;toolbar:false\">enctype=&quot;multipart/form-data&quot;</pre><p><strong>禁用IE浏览器input的删除按钮</strong></p><pre class=\"brush:css;toolbar:false\">input::-ms-clear{display:none;}</pre><p><strong>透明效果</strong></p><pre class=\"brush:css;toolbar:false\">&nbsp;filter:alpha(opacity=80);-moz-opacity:0.8;-khtml-opacity:&nbsp;0.8;opacity:&nbsp;0.8;</pre><p><strong>360浏览器input自动填充淡黄色背景怎么解决</strong></p><pre class=\"brush:css;toolbar:false\">//给input设置内置阴影！而且一定要大，至少要比你的input本身大\r\ninput:-webkit-autofill&nbsp;{-webkit-box-shadow:&nbsp;0&nbsp;0&nbsp;0px&nbsp;1000px&nbsp;white&nbsp;inset;}</pre><p><strong>根据不同分辨率调用不同CSS样式</strong></p><pre class=\"brush:css;toolbar:false\">@media&nbsp;screen&nbsp;and&nbsp;(min-height:800px)&nbsp;and&nbsp;(max-height:900px){\r\n&nbsp;.box_top{margin-top:70px;}\r\n}\r\n\r\n@media&nbsp;screen&nbsp;and&nbsp;(min-height:901px)&nbsp;and&nbsp;(max-height:1050px){\r\n&nbsp;.box_top{margin-top:90px;}\r\n}\r\n\r\n@media&nbsp;screen&nbsp;and&nbsp;(min-height:1051px)&nbsp;{\r\n&nbsp;.box_top{margin-top:120px;}\r\n}</pre><p><strong>Select分组</strong></p><pre class=\"brush:html;toolbar:false\">&lt;select&gt;\r\n&nbsp;&nbsp;&lt;optgroup&nbsp;label=&quot;Swedish&nbsp;Cars&quot;&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&nbsp;value&nbsp;=&quot;volvo&quot;&gt;Volvo&lt;/option&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&nbsp;value&nbsp;=&quot;saab&quot;&gt;Saab&lt;/option&gt;\r\n&nbsp;&nbsp;&lt;/optgroup&gt;\r\n\r\n&nbsp;&nbsp;&lt;optgroup&nbsp;label=&quot;German&nbsp;Cars&quot;&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&nbsp;value&nbsp;=&quot;mercedes&quot;&gt;Mercedes&lt;/option&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;option&nbsp;value&nbsp;=&quot;audi&quot;&gt;Audi&lt;/option&gt;\r\n&nbsp;&nbsp;&lt;/optgroup&gt;\r\n&lt;/select&gt;</pre><p><br/></p>', '2017-03-01 16:07:47', '2', '1', '12000');
INSERT INTO `lei_daima` VALUES ('9', '360浏览器默认使用webkit内核', '<pre class=\"brush:html;toolbar:false;\">&lt;head&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;content=&quot;webkit&quot;&nbsp;name=&quot;renderer&quot;&nbsp;/&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;name=&quot;viewport&quot;&nbsp;content=&quot;width=device-width,&nbsp;initial-scale=1.0,&nbsp;maximum-scale=1.0,&nbsp;user-scalable=no&quot;&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;http-equiv=&quot;X-UA-Compatible&quot;&nbsp;content=&quot;IE=Edge,chrome=1&quot;&gt;\r\n&lt;/head&gt;</pre><p>其中：&lt;meta content=&quot;webkit&quot; name=&quot;renderer&quot; /&gt;必须紧跟&lt;head&gt;</p><p>以上代码IE=edge告诉IE使用最新的引擎渲染网页，chrome=1则可以激活Chrome Frame</p>', '2017-03-01 16:07:57', '2', '1', '20000');
INSERT INTO `lei_daima` VALUES ('10', 'CSS透明效果', '', '2017-03-01 16:08:17', '2', '0', '10000');
INSERT INTO `lei_daima` VALUES ('11', '事件加载、元素选择器及常用属性', '<p><strong>元素选择器：</strong></p><pre class=\"brush:js;toolbar:false\">document.getElementById(&quot;myHeader&quot;)\r\ndocument.getElementsByName(&quot;myHeader&quot;)\r\n\r\ndocument.getElementById(&#39;myAnchor&#39;).innerHTML=&quot;W3Schools&quot;;&nbsp;//innerHTML&nbsp;属性设置或返回元素的&nbsp;inner&nbsp;HTML。\r\ndocument.getElementById(&#39;myAnchor&#39;).href=&quot;http://www.w3schools.com&quot;;&nbsp;\r\ndocument.getElementById(&#39;myAnchor&#39;).target=&quot;_blank&quot;;\r\ndocument.getElementById(&#39;myid&#39;).className;&nbsp;//返回元素的&nbsp;class&nbsp;属性。</pre><p><strong>加载事件：</strong></p><pre class=\"brush:js;toolbar:false\">$(document).ready(function(){\r\n&nbsp;&nbsp;$(&quot;.btn1&quot;).click(function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;$(&quot;p&quot;).slideToggle();\r\n&nbsp;&nbsp;});\r\n});\r\n\r\n//提示：ready()&nbsp;函数不应与&nbsp;&lt;body&nbsp;onload=&quot;&quot;&gt;&nbsp;一起使用。\r\n//当&nbsp;DOM（文档对象模型）&nbsp;已经加载，并且页面（包括图像）已经完全呈现时，会发生&nbsp;ready&nbsp;事件。\r\n//由于该事件在文档就绪后发生，因此把所有其他的&nbsp;jQuery&nbsp;事件和函数置于该事件中是非常好的做法。</pre><p><strong>JQ通过class名循环读取元素值</strong></p><pre class=\"brush:js;toolbar:false\">var&nbsp;bugstr_5&nbsp;=&nbsp;document.exam1.tkxuan.value;\r\nvar&nbsp;bugstr_5=bugstr_5.replace(eval(&#39;/,/g&#39;),&#39;{|}&#39;);\r\nvar&nbsp;bugstr1_5_in=bugstr_5.split(&quot;{|}&quot;);\r\nvar&nbsp;inputs&nbsp;=&nbsp;$(&quot;.tiankong&quot;);\r\nii=0;\r\ninputs.each(function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;$(this).val(bugstr1_5_in[ii])\r\n&nbsp;&nbsp;&nbsp;&nbsp;//if&nbsp;($(this).is(&quot;:checked&quot;))&nbsp;{i++;return&nbsp;false}//检测到一个就跳出循环\r\n&nbsp;&nbsp;&nbsp;&nbsp;ii++;\r\n});</pre><p><strong>JQ模拟click事件</strong></p><pre class=\"brush:js;toolbar:false\">$(&#39;.close-reveal-modal&#39;).click();</pre>', '2017-03-01 16:08:34', '1', '1', '12000');
INSERT INTO `lei_daima` VALUES ('12', 'AppServ常规参数配置手册', '<p><strong>一、PHP.INI文件配置</strong></p><pre class=\"brush:php;toolbar:false\">1.全局变量关闭：register_globals&nbsp;=&nbsp;Off，正式运行网站请关闭此选项。\r\n2.设置magic_quotes_gpc&nbsp;=&nbsp;On&nbsp;开启gpc功能\r\n3.extension=php_mbstring.dll&nbsp;&nbsp;PHP的扩展设置mbstring，没有&nbsp;mbstring&nbsp;扩展的&nbsp;phpMyAdmin&nbsp;不能正确识别字符串，可能产生不可意料的结果\r\n4.extension=php_openssl.dll&nbsp;&nbsp;开启此项，方可使用file_get_contents访问https\r\n5.session.save_path一定要设置到一个可以读写的路径，如&nbsp;D:/session\r\nPHP上传文件设置：（通过echo&nbsp;ini_get(&#39;post_max_size&#39;)&nbsp;判断当前值）\r\n1、PHP没有开启文件上传功能，配置应该为&nbsp;file_uploads&nbsp;=&nbsp;On\r\n2、PHP配置上传大小限制，upload_max_filesize&nbsp;=&nbsp;20M\r\n3、PHP配置的POST方式大小限制，post_max_size&nbsp;=&nbsp;80M\r\n\r\n//------------------------------------------------------------------------\r\n\r\n一、关于PHP7版本，部分需要设置项（PHP&nbsp;7.0.3）\r\n（1）设置&nbsp;default_charset&nbsp;=’GBK’\r\n（2）session.save_path=”D:session”\r\n（3）short_open_tag&nbsp;=&nbsp;on&nbsp;&nbsp;开启短标签\r\n（4）error_reporting,修改为error_reporting&nbsp;=&nbsp;E_ALL&nbsp;&amp;&nbsp;~E_NOTICE\r\n\r\n二、关于PHP7对部分程序的调整\r\n（1）return&nbsp;htmlspecialchars($str,ENT_COMPAT,&#39;ISO-8859-1&#39;);\r\n（2）防止PHPEXCEL插件报出以下错误：&#39;break&#39;&nbsp;not&nbsp;in&nbsp;the&nbsp;&#39;loop&#39;&nbsp;or&nbsp;&#39;switch&#39;&nbsp;context\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;直接删除文件（PHPExcelCalculationFunctions.php）的576行的&nbsp;&#39;break&#39;即可！\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;网上有人说是高版本的php7.0才有的，因为：\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;As&nbsp;break&nbsp;is&nbsp;after&nbsp;return&nbsp;statement,&nbsp;so&nbsp;it&nbsp;giving&nbsp;fatal&nbsp;error.\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;因为break在return之后，报了个语法错误！确实不对！\r\n（3）number_format()函数支持一个、两个或四个参数（不是三个）。</pre><p><strong>二、httpd.conf文件配置</strong></p><p>1.修改默认首页：DirectoryIndex index.html加上index.php</p><p>2.限制目录列表显示：Options Indexes FollowSymLinks 删除Indexes</p><p>3.启用rewrite和.htaccess</p><p>第一步，httpd.conf 文件里 查找“&lt;Directory /&gt;”把里面的 AllowOverride None 改成 AllowOverride all 即可，改完后的代码如下：&nbsp;</p><pre class=\"brush:html;toolbar:false\">&lt;Directory&nbsp;/&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;Options&nbsp;FollowSymLinks\r\n&nbsp;&nbsp;&nbsp;&nbsp;AllowOverride&nbsp;all\r\n&nbsp;&nbsp;&nbsp;&nbsp;Order&nbsp;deny,allow\r\n&nbsp;&nbsp;&nbsp;&nbsp;allow&nbsp;from&nbsp;all\r\n&lt;/Directory&gt;</pre><p>第二步，查找到“LoadModule rewrite_module”，将前面的&quot;#&quot;号删除即可；如果没有查找到，则到“LoadModule”区域，在最后一行加入“LoadModule rewrite_module modules/mod_rewrite.so”（独占一行）。</p><p>4.绑定域名</p><pre class=\"brush:php;toolbar:false\">DocumentRoot&nbsp;&quot;D:/AppServ/www&quot;\r\nServerName&nbsp;192.168.1.100:80\r\nNameVirtualHost&nbsp;192.168.1.100:80\r\n\r\n&lt;VirtualHost&nbsp;192.168.1.100:80&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;DocumentRoot&nbsp;&quot;D:/AppServ/www/&quot;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;ServerName&nbsp;www.abc.dooooo\r\n&lt;/VirtualHost&gt;\r\n\r\n&lt;VirtualHost&nbsp;192.168.1.100:80&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;DocumentRoot&nbsp;&quot;D:/AppServ/www/mm1&quot;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;ServerName&nbsp;m.abc.com\r\n&nbsp;&nbsp;&nbsp;&nbsp;ServerAlias&nbsp;e.abc.com&nbsp;&nbsp;abc.com\r\n&lt;/VirtualHost&gt;\r\n\r\n&lt;VirtualHost&nbsp;192.168.1.100:80&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;DocumentRoot&nbsp;&quot;D:/AppServ/www/mm2&quot;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;ServerName&nbsp;e.bcd.com\r\n&lt;/VirtualHost&gt;</pre><p><strong><br/></strong></p><p><strong>三、更改phpmyadmin目录，并将默认目录设置空白</strong></p><p>1.将phpmyadmin目录更换其它名称；</p><p>2.将默认根目录添加index.html空白文件；</p><p><strong><br/></strong></p><p><strong>四、设置.htaccess强制转向HTTPS</strong></p><pre class=\"brush:xml;toolbar:false\">//实现效果：\r\n//1&nbsp;&nbsp;非https转向https\r\n//2&nbsp;&nbsp;非www开头转向www\r\n\r\nRewriteEngine&nbsp;on\r\nRewriteCond&nbsp;%{HTTP:From-Https}&nbsp;!^on$&nbsp;[NC]\r\nRewriteRule&nbsp;^(.*)$&nbsp;https://www.mdaima.com/$1&nbsp;[R=301,L]\r\n\r\nRewriteCond&nbsp;%{HTTP_HOST}&nbsp;!^www.&nbsp;\r\nRewriteRule&nbsp;^(.*)$&nbsp;https://www.mdaima.com/$1&nbsp;[R=301,L]\r\n\r\n//指定IP可以访问指定后缀文件\r\n&lt;Files&nbsp;~&nbsp;&quot;.(sql|zip_bz)$&quot;&gt;\r\n	order&nbsp;deny,allow\r\n	deny&nbsp;from&nbsp;all\r\n	allow&nbsp;from&nbsp;127.0.0.1\r\n&lt;/Files&gt;</pre><p>更多详细配置，请查看：<a href=\"https://www.mdaima.com/news/86.html\" _src=\"https://www.mdaima.com/news/86.html\" target=\"_blank\">https://www.mdaima.com/news/86.html</a></p>', '2017-03-01 16:08:44', '3', '1', '10000');
INSERT INTO `lei_daima` VALUES ('14', '字符串与数组间转换', '', '2017-03-01 16:09:05', '4', '0', '10000');
INSERT INTO `lei_daima` VALUES ('15', '日期、时间、时间戳', '<p><strong>获取当前日期：</strong></p><pre class=\"brush:php;toolbar:false\">&lt;?&nbsp;$indate=date(&quot;Y-m-d&nbsp;H:i:s&quot;)&nbsp;;?&gt;</pre><p><strong>获取当前时间戳：</strong><br/></p><pre class=\"brush:php;toolbar:false\">&lt;?=time()?&gt;</pre><p><strong>日期格式化：</strong><br/></p><pre class=\"brush:php;toolbar:false\">&lt;?=date(&quot;Y-m-d&nbsp;H:i:s&quot;,strtotime(&quot;2016-12-12&nbsp;12:12:12&quot;))?&gt;</pre><p><strong>日期N天后或前的日期计算</strong></p><pre class=\"brush:php;toolbar:false\">$date1_search=date(&quot;Y-m-d&quot;,strtotime(&quot;-6&nbsp;day&quot;));//当前日期的6天前的日期\r\n$date1_search=date(&quot;Y-m-d&quot;,strtotime($date1_var)+(86400*$u));//$date1_var为变量，86400为一天的秒数，$u为天数,加前号表示前或后</pre><p><strong>计算两个日期间的天数</strong></p><pre class=\"brush:php;toolbar:false\">function&nbsp;daysbetweendates($date1,&nbsp;$date2){&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;$date1&nbsp;=&nbsp;strtotime($date1);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;$date2&nbsp;=&nbsp;strtotime($date2);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;$days&nbsp;=&nbsp;ceil(abs($date1&nbsp;-&nbsp;$date2)/86400);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;$days;&nbsp;\r\n}\r\n$date_num=daysbetweendates($date1_search,&nbsp;$date2_search);</pre><p><strong>附表：</strong><br/></p><pre class=\"brush:html;toolbar:false\">d&nbsp;-&nbsp;一个月中的第几天（从&nbsp;01&nbsp;到&nbsp;31）\r\nD&nbsp;-&nbsp;星期几的文本表示（用三个字母表示）\r\nj&nbsp;-&nbsp;一个月中的第几天，不带前导零（1&nbsp;到&nbsp;31）\r\nl（&#39;L&#39;&nbsp;的小写形式）-&nbsp;星期几的完整的文本表示\r\nN&nbsp;-&nbsp;星期几的&nbsp;ISO-8601&nbsp;数字格式表示（1表示Monday[星期一]，7表示Sunday[星期日]）\r\nS&nbsp;-&nbsp;一个月中的第几天的英语序数后缀（2&nbsp;个字符：st、nd、rd&nbsp;或&nbsp;th。与&nbsp;j&nbsp;搭配使用）\r\nw&nbsp;-&nbsp;星期几的数字表示（0&nbsp;表示&nbsp;Sunday[星期日]，6&nbsp;表示&nbsp;Saturday[星期六]）\r\nz&nbsp;-&nbsp;一年中的第几天（从&nbsp;0&nbsp;到&nbsp;365）\r\nW&nbsp;-&nbsp;用&nbsp;ISO-8601&nbsp;数字格式表示一年中的星期数字（每周从&nbsp;Monday[星期一]开始）\r\nF&nbsp;-&nbsp;月份的完整的文本表示（January[一月份]&nbsp;到&nbsp;December[十二月份]）\r\nm&nbsp;-&nbsp;月份的数字表示（从&nbsp;01&nbsp;到&nbsp;12）\r\nM&nbsp;-&nbsp;月份的短文本表示（用三个字母表示）\r\nn&nbsp;-&nbsp;月份的数字表示，不带前导零（1&nbsp;到&nbsp;12）\r\nt&nbsp;-&nbsp;给定月份中包含的天数\r\nL&nbsp;-&nbsp;是否是闰年（如果是闰年则为&nbsp;1，否则为&nbsp;0）\r\no&nbsp;-&nbsp;ISO-8601&nbsp;标准下的年份数字\r\nY&nbsp;-&nbsp;年份的四位数表示\r\ny&nbsp;-&nbsp;年份的两位数表示\r\na&nbsp;-&nbsp;小写形式表示：am&nbsp;或&nbsp;pm\r\nA&nbsp;-&nbsp;大写形式表示：AM&nbsp;或&nbsp;PM\r\nB&nbsp;-&nbsp;Swatch&nbsp;Internet&nbsp;Time（000&nbsp;到&nbsp;999）\r\ng&nbsp;-&nbsp;12&nbsp;小时制，不带前导零（1&nbsp;到&nbsp;12）\r\nG&nbsp;-&nbsp;24&nbsp;小时制，不带前导零（0&nbsp;到&nbsp;23）\r\nh&nbsp;-&nbsp;12&nbsp;小时制，带前导零（01&nbsp;到&nbsp;12）\r\nH&nbsp;-&nbsp;24&nbsp;小时制，带前导零（00&nbsp;到&nbsp;23）\r\ni&nbsp;-&nbsp;分，带前导零（00&nbsp;到&nbsp;59）\r\ns&nbsp;-&nbsp;秒，带前导零（00&nbsp;到&nbsp;59）\r\nu&nbsp;-&nbsp;微秒（PHP&nbsp;5.2.2&nbsp;中新增的）\r\ne&nbsp;-&nbsp;时区标识符（例如：UTC、GMT、Atlantic/Azores）\r\nI（i&nbsp;的大写形式）-&nbsp;日期是否是在夏令时（如果是夏令时则为&nbsp;1，否则为&nbsp;0）\r\nO&nbsp;-&nbsp;格林威治时间（GMT）的差值，单位是小时（实例：+0100）\r\nP&nbsp;-&nbsp;格林威治时间（GMT）的差值，单位是&nbsp;hours:minutes（PHP&nbsp;5.1.3&nbsp;中新增的）\r\nT&nbsp;-&nbsp;时区的简写（实例：EST、MDT）\r\nZ&nbsp;-&nbsp;以秒为单位的时区偏移量。UTC&nbsp;以西时区的偏移量为负数（-43200&nbsp;到&nbsp;50400）\r\nc&nbsp;-&nbsp;ISO-8601&nbsp;标准的日期（例如&nbsp;2013-05-05T16:34:42+00:00）\r\nr&nbsp;-&nbsp;RFC&nbsp;2822&nbsp;格式的日期（例如&nbsp;Fri,&nbsp;12&nbsp;Apr&nbsp;2013&nbsp;12:01:05&nbsp;+0200）\r\nU&nbsp;-&nbsp;自&nbsp;Unix&nbsp;纪元（January&nbsp;1&nbsp;1970&nbsp;00:00:00&nbsp;GMT）以来经过的秒数</pre><p><br/></p>', '2017-03-01 16:09:16', '4', '1', '15000');
INSERT INTO `lei_daima` VALUES ('16', 'base64_decode与base64_encode加密解密函数', '<pre class=\"brush:php;toolbar:false\">$str=&#39;d3d3LmpiNTEubmV0IOiEmuacrOS5i+Wutg==&#39;;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//定义字符串&nbsp;\r\necho&nbsp;base64_decode($str);&nbsp;//输出解码后的内容\r\n\r\n$str=&#39;www.jb51.net&nbsp;脚本之家&#39;;&nbsp;//定义字符串&nbsp;\r\necho&nbsp;base64_encode($str);&nbsp;&nbsp;//&nbsp;输出编码后的内容为：&nbsp;d3d3LmpiNTEubmV0IOiEmuacrOS5i+Wutg==</pre><p><br/></p>', '2017-03-01 16:09:26', '4', '1', '10000');
INSERT INTO `lei_daima` VALUES ('17', 'PHP页面及字符编码转换', '<p>PHP字符编码转换：</p><pre class=\"brush:php;toolbar:false\">iconv(&#39;gbk&#39;,&#39;utf-8&#39;,$message);</pre><p>页面编码</p><pre class=\"brush:php;toolbar:false\">header(&quot;Content-Type:&nbsp;text/html;charset=GB2312&quot;);</pre><p><br/></p>', '2017-03-01 16:09:36', '4', '1', '10000');
INSERT INTO `lei_daima` VALUES ('18', '身份证取生日和性别', '<p><strong>通过身份证获取出生日期：</strong></p><pre class=\"brush:php;toolbar:false\">$birthday=substr($sfz,6,4).&quot;-&quot;.substr($sfz,10,2).&quot;-&quot;.substr($sfz,12,2);</pre><p><strong>通过身份证获取性别：</strong></p><pre class=\"brush:php;toolbar:false\">if&nbsp;(substr($sfz,16,1)&nbsp;%&nbsp;2==0){\r\n&nbsp;&nbsp;&nbsp;&nbsp;$xingbie=&#39;女&#39;;\r\n}else{\r\n&nbsp;&nbsp;&nbsp;&nbsp;$xingbie=&#39;男&#39;;\r\n}</pre><p><br/></p>', '2017-03-01 16:10:01', '4', '1', '15000');
INSERT INTO `lei_daima` VALUES ('20', 'MYSQL最大连接数查询及状态', '<pre class=\"brush:sql;toolbar:false;\">//查看数据库最大连接数，实际MySQL服务器允许的最大连接数16384；\r\nshow&nbsp;variables&nbsp;like&nbsp;&#39;%max_connections%&#39;;\r\n\r\n//设置这个最大连接数值\r\nset&nbsp;GLOBAL&nbsp;max_connections=256;\r\n\r\n//查看服务器响应的最大连接数:\r\nshow&nbsp;global&nbsp;status&nbsp;like&nbsp;&#39;Max_used_connections&#39;;</pre><p><br/></p>', '2017-03-01 16:10:22', '5', '1', '10000');
INSERT INTO `lei_daima` VALUES ('21', '字符串与数组转换，循环输出', '<p><strong>以数组形式提交的表单，获取数组元素并组合成字符串</strong></p><pre class=\"brush:php;toolbar:false\">if&nbsp;($post!=&#39;&#39;)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;$post_str&nbsp;=&nbsp;&#39;,&#39;.implode(&quot;,&quot;,$post).&#39;,&#39;;//两端带半角逗号，形式闭环字符串\r\n}</pre><p><strong>将字符串转为数组并循环转出：</strong><br/></p><pre class=\"brush:php;toolbar:false\">$keyword_array=explode(&#39;,&#39;,$keyword);\r\n$keyword_str=&#39;&#39;;\r\n\r\nfor&nbsp;($b=0;$b&lt;count($keyword_array);$b++){\r\n	$sql=&quot;select&nbsp;*&nbsp;from&nbsp;abc&nbsp;where&nbsp;id=&#39;&quot;.$keyword_array[$b].&quot;&#39;&nbsp;order&nbsp;by&nbsp;px&quot;;\r\n	$result=$mysqli-&gt;query($sql);\r\n	while&nbsp;($rs=$result-&gt;fetch_assoc()){\r\n		$keyword_str.=$rs[&quot;keyword&quot;].&quot;,&quot;;\r\n	}\r\n}</pre><p><br/></p>', '2017-03-02 09:40:39', '4', '1', '10000');
INSERT INTO `lei_daima` VALUES ('22', '字符串截取、长度、类型判断等', '<p><strong>字符串截取：</strong><br/></p><pre class=\"brush:php;toolbar:false\">if&nbsp;($keyword_str!=&#39;&#39;){\r\n	echo&nbsp;substr($keyword_str,0,-1);//去掉最后一位\r\n}</pre><p><strong>判断指定字符串是否存在：</strong></p><pre class=\"brush:php;toolbar:false\">if&nbsp;(strpos($dh_url,&quot;/jingyan&quot;)!==false){\r\n	$class_2=&#39;class=&quot;dh_focus&quot;&#39;;//存在时的处理\r\n}</pre><p><strong>判断是否为数字及Input格式化为数字：</strong></p><pre class=\"brush:html;toolbar:false\">if&nbsp;(!is_numeric($id)){\r\n	echo&nbsp;&quot;No&nbsp;num&quot;;\r\n	exit;\r\n}\r\n\r\n&lt;input&nbsp;name=&quot;px&quot;&nbsp;type=&quot;text&quot;&nbsp;value=&quot;&quot;&nbsp;onKeyUp=&quot;this.value=this.value.replace(/D/g,&#39;&#39;);&quot;&nbsp;onafterpaste=&quot;this.value=this.value.replace(/D/g,&#39;&#39;)&quot;/&gt;</pre><p><strong>字符串长度：</strong></p><pre class=\"brush:php;toolbar:false\">$str&nbsp;=&nbsp;‘aaaaaa&#39;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;if(strlen($str)&nbsp;&gt;&nbsp;6){\r\n&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;&quot;字符串大于6&quot;;\r\n}</pre><p><strong>通过数组判断字符串是否在多个字符串中匹配</strong></p><pre class=\"brush:php;toolbar:false\">&lt;?php\r\n$people&nbsp;=&nbsp;array(&quot;Bill&quot;,&nbsp;&quot;Steve&quot;,&nbsp;&quot;Mark&quot;,&nbsp;&quot;David&quot;);\r\n\r\nif&nbsp;(in_array(&quot;Mark&quot;,&nbsp;$people))\r\n&nbsp;&nbsp;{\r\n&nbsp;&nbsp;echo&nbsp;&quot;匹配已找到&quot;;\r\n&nbsp;&nbsp;}\r\nelse\r\n&nbsp;&nbsp;{\r\n&nbsp;&nbsp;echo&nbsp;&quot;匹配未找到&quot;;\r\n&nbsp;&nbsp;}\r\n?&gt;</pre><p><strong>替换多个空格为一个</strong></p><pre class=\"brush:php;toolbar:false\">$jieguo&nbsp;=&nbsp;preg_replace(&quot;/(s+)/&quot;,&#39;&nbsp;&#39;,$jieguo);</pre><p><br/></p>', '2017-03-02 09:42:23', '4', '1', '13000');
INSERT INTO `lei_daima` VALUES ('23', 'js对字符串格式化', '<p><strong>转整数</strong></p><pre class=\"brush:js;toolbar:false\">parseInt(row)</pre><p><strong>把给定的值转换成数字（可以是整数或浮点数）</strong><br/></p><pre class=\"brush:js;toolbar:false\">Number(value)</pre><p><strong>替换指定字符</strong></p><pre class=\"brush:js;toolbar:false\">var&nbsp;str&nbsp;=&nbsp;&#39;abcadeacf&#39;;\r\nvar&nbsp;str1&nbsp;=&nbsp;str.replace(&#39;a&#39;,&nbsp;&#39;o&#39;);\r\nalert(str1);&nbsp;&nbsp;\r\n//&nbsp;打印结果：&nbsp;obcadeacf&nbsp;\r\n//&nbsp;只会替换第一个匹配的字符\r\n&nbsp;\r\nvar&nbsp;str2&nbsp;=&nbsp;str.replace(/a/g,&nbsp;&#39;o&#39;);\r\nalert(str2);&nbsp;&nbsp;\r\n//打印结果：&nbsp;obcodeocf\r\n//注意：&nbsp;此处replace的第一个参数为正则表达式，/g是全文匹配标识。如特殊字符需转义str.replace(/\\-/g,&quot;o&quot;)</pre><p><br/></p>', '2017-12-20 11:23:49', '1', '1', '50000');
INSERT INTO `lei_daima` VALUES ('24', 'JQ对CSS和属性的设置', '<p><strong>JQ对input属性disabled的设置</strong></p><pre class=\"brush:js;toolbar:false\">//两种方法设置disabled属性&nbsp;\r\n$(&#39;#areaSelect&#39;).attr(&quot;disabled&quot;,true);&nbsp;\r\n$(&#39;#areaSelect&#39;).attr(&quot;disabled&quot;,&quot;disabled&quot;);&nbsp;\r\n\r\n//三种方法移除disabled属性&nbsp;\r\n$(&#39;#areaSelect&#39;).attr(&quot;disabled&quot;,false);&nbsp;\r\n$(&#39;#areaSelect&#39;).removeAttr(&quot;disabled&quot;);&nbsp;\r\n$(&#39;#areaSelect&#39;).attr(&quot;disabled&quot;,&quot;&quot;);</pre><p><strong>1、javascript addClass</strong></p><p>利用document.getElementById(&quot;XX&quot;)找到document对象、然后再通过addClass(&quot;xxx&quot;)修改其class值</p><pre class=\"brush:js;toolbar:false\">document.getElementById(&quot;XX&quot;).addClass(&quot;xxx&quot;);</pre><p><strong>2、addClass()</strong></p><p>addClass()方法向匹配的元素增加指定的类名(一个或多个)、注意对于元素来说、class属性可以有多个值、该方法不会移除已经存在的值、而是在原有的基础上追加一个或多个class属性</p><pre class=\"brush:js;toolbar:false\">$(&quot;.XX&quot;).addClass(&quot;xxx&quot;);</pre><p><strong>3、removeClass()</strong></p><p>removeClass()方法从被选元素移除一个或多个类、如需移除若干类、用空格来分隔类名、如果没有传参数、该方法将会移除被选元素的所有类</p><pre class=\"brush:js;toolbar:false\">$(&quot;.XX&quot;).removeClass(&quot;xxx&quot;);</pre><p><strong>4、toggleClass()</strong></p><p>toggleClass()方法对被选元素的一个或多个类进行切换(设置或移除)、该方法检查每个元素中的指定类、如果存在则删除、如果不存在则添加、通过添加参数,可以设置只进行删除或者只进行添加操作、switch值为true时、只添加、为false时、只删除</p><pre class=\"brush:js;toolbar:false\">$(&quot;.XX&quot;).toggleClass(&quot;xxx&quot;,switch);</pre><p><strong>5、hasClass()</strong></p><p>hasClass()方法检查被选元素是否包含指定的class、还可以用is()方法实现同样的功能、方法参数传入一个选择器字符串、比如”.className”</p><pre class=\"brush:js;toolbar:false\">$(&quot;.XX&quot;).hasClass(&quot;xxx&quot;);</pre><p><strong>6、css()</strong></p><p>读操作：获取匹配元素集合中第一个元素的指定样式值(一个或多个)、读取多个样式值的操作是在jQuery v1.9才加入的</p><p>写操作: 为匹配元素集合中的每一个元素设置一个或多个CSS属性的值、传入的参数可以是单个的键值对、也可以是PlainObject指定的多个值</p><pre class=\"brush:js;toolbar:false\">$(&quot;.XX&quot;).css(&quot;color&quot;,&quot;red&quot;);</pre><p><strong>7、attr()</strong></p><p>attr()方法设置或返回被选元素的属性值、根据该方法不同的参数、其工作方式也有所差异</p><pre class=\"brush:js;toolbar:false\">$(&quot;.XX&quot;).attr(&quot;class&quot;,&quot;abc&nbsp;bcd&nbsp;def&quot;);</pre><p>jQuery会处理各个浏览器中不太相同的一些具体情况、比如W3C的cssFloat在IE中是styleFloat、在jQuery中、你可以使用float、然后jQuery会帮你把它翻译成每个浏览器中应该有的正确名称、还有、jQuery会合理解读CSS和DOM格式的多词属性、比如.css(“background-color”)和.css(“backgroundColor”)、它都是可以处理的</p><p><br/></p>', '2018-01-12 09:04:44', '1', '1', '14000');
INSERT INTO `lei_daima` VALUES ('25', 'PHP判断文件是否存在', '<p><strong>如果图片是空的或是不存在则用指定的替换</strong></p><pre class=\"brush:php;toolbar:false\">if&nbsp;($img==&#39;&#39;&nbsp;||&nbsp;!file_exists($img)&nbsp;){\r\n	$img=&quot;/sc/touxiang.png&quot;;\r\n}</pre>', '2018-01-25 08:26:01', '4', '1', '10000');
INSERT INTO `lei_daima` VALUES ('26', 'MySQL修改root密码的多种方法', '<p><strong>方法1： 用SET PASSWORD命令</strong></p><pre class=\"brush:sql;toolbar:false\">MySQL&nbsp;-u&nbsp;root\r\nmysql&gt;&nbsp;SET&nbsp;PASSWORD&nbsp;FOR&nbsp;&#39;root&#39;@&#39;localhost&#39;&nbsp;=&nbsp;PASSWORD(&#39;newpass&#39;);</pre><p><strong>方法2：用mysqladmin</strong><br/></p><pre class=\"brush:sql;toolbar:false\">mysqladmin&nbsp;-u&nbsp;root&nbsp;password&nbsp;&quot;newpass&quot;</pre><p>如果root已经设置过密码，采用如下方法<br/></p><pre class=\"brush:sql;toolbar:false\">mysqladmin&nbsp;-u&nbsp;root&nbsp;password&nbsp;oldpass&nbsp;&quot;newpass&quot;</pre><p><strong>方法3： 用UPDATE直接编辑user表</strong><br/></p><pre class=\"brush:sql;toolbar:false\">mysql&nbsp;-u&nbsp;root\r\nmysql&gt;&nbsp;use&nbsp;mysql;\r\nmysql&gt;&nbsp;UPDATE&nbsp;user&nbsp;SET&nbsp;Password&nbsp;=&nbsp;PASSWORD(&#39;newpass&#39;)&nbsp;WHERE&nbsp;user&nbsp;=&nbsp;&#39;root&#39;;\r\nmysql&gt;&nbsp;FLUSH&nbsp;PRIVILEGES;</pre><p><strong>在丢失root密码的时候，可以这样</strong></p><pre class=\"brush:sql;toolbar:false\">mysqld_safe&nbsp;--skip-grant-tables&amp;\r\nmysql&nbsp;-u&nbsp;root&nbsp;mysql\r\nmysql&gt;&nbsp;UPDATE&nbsp;user&nbsp;SET&nbsp;password=PASSWORD(&quot;new&nbsp;password&quot;)&nbsp;WHERE&nbsp;user=&#39;root&#39;;\r\nmysql&gt;&nbsp;FLUSH&nbsp;PRIVILEGES;</pre><p>&nbsp; &nbsp;</p><p><br/></p><p><br/></p>', '2018-04-23 08:33:25', '5', '1', '15000');
INSERT INTO `lei_daima` VALUES ('27', '利用ab工具测试apache压力', '<p>利用ab工具测试apache压力</p><pre class=\"brush:xml;toolbar:false\">ab&nbsp;-c800&nbsp;-n10000&nbsp;/&nbsp;-t5&nbsp;http://192.168.1.1</pre><p>-c 并发连接数</p><p>-n总连接数</p><p>-t完成时间，单位秒</p>', '2018-10-23 10:14:31', '3', '1', '11000');
INSERT INTO `lei_daima` VALUES ('28', 'Windows下Apache及Mysql配置并发', '<p style=\"margin-top:0;margin-right:0;margin-bottom:16px;margin-left: 0;line-height:150%;background:white\"><strong>Windows下Apache配置（Httpd.conf）</strong></p><p>开启模块:（仅在需监控服务器性能时开启）</p><pre class=\"brush:xml;toolbar:false\">LoadModule&nbsp;status_module&nbsp;modules/mod_status.so\r\nLoadModule&nbsp;info_module&nbsp;modules/mod_info.so</pre><p>______________________________________________________________________</p><p>启用关联引用文件:</p><p>#Include&nbsp;conf/extra/httpd-mpm.conf（重要）<br/></p><pre class=\"brush:xml;toolbar:false\">&lt;IfModule&nbsp;mpm_winnt_module&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;ThreadsPerChild&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1920&nbsp;&nbsp;#可能会受限制&nbsp;1920\r\n&nbsp;&nbsp;&nbsp;&nbsp;MaxRequestsPerChild&nbsp;&nbsp;&nbsp;&nbsp;0\r\n&lt;/IfModule&gt;</pre><p>______________________________________________________________________</p><p>#Include conf/extra/httpd-default.conf（重要）</p><pre class=\"brush:xml;toolbar:false\">Timeout&nbsp;20\r\n//该参数为连接超时，缺省的300秒显然太大，缩小该参数就会减少同时连接数，即用户实际占用的线程数，这样就可以起到配合上面ThreadsPerChlid参数的作用。\r\nKeepAlive&nbsp;On\r\n//该参数为是否保持活连接，目前网站中一个页面一般会包含多个文件，所以相应用户访问时会有多个请求，因此开启可以提高服务器性能。\r\nMaxKeepAliveRequests&nbsp;50\r\n//该参数为最大的活连接请求数，可以根据网页实际包含的文件数目自行调节。\r\nKeepAliveTimeout&nbsp;5\r\n//该参数为活连接的超时时间，一般只要设置成小于Timeout即可。</pre><p>______________________________________________________________________</p><p>#Include conf/extra/httpd-info.conf（仅在需监控服务器性能时开启）</p><pre class=\"brush:xml;toolbar:false\">&lt;location&nbsp;/server-status&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SetHandler&nbsp;server-status&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Order&nbsp;Deny,Allow\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Deny&nbsp;from&nbsp;all\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Allow&nbsp;from&nbsp;10.64.201.251\r\n&lt;/location&gt;\r\n\r\n或者以下形式\r\n\r\n&lt;location&nbsp;/server-info-lilei&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;SetHandler&nbsp;server-status\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Allow&nbsp;from&nbsp;all\r\n&lt;/location&gt;\r\n\r\nExtendedStatus&nbsp;On</pre><p>配置完成，重新启动apache（注意，不能直接restart，而要先stop，再start）</p><p>可以用http://10.1.1.15/server-status?refresh=N将表示访问状态页面可以每N秒自动刷新一次；（http://10.1.1.5/server-info-lilei）</p><p>Deny&nbsp;from表示禁止的访问地址；</p><p>Allow&nbsp;from表示允许的地址访问；</p><p>ExtendedStatus&nbsp;On&nbsp;表示的是待会访问的时候能看到详细的请求信息，另外该设置仅能用于全局设置，不能在特定的虚拟主机中打开或关闭。启用扩展状态信息将会导致服务器运行效率降低。</p><p style=\"margin-top:21px;margin-right:0;margin-bottom:21px;margin-left: 0;line-height:150%;background:white\"><strong>______________________________________________________________________</strong></p><p style=\"margin-top:21px;margin-right:0;margin-bottom:21px;margin-left: 0;line-height:150%;background:white\"><strong>windows下mysql配置（my.ini）</strong></p><p>max_connections=10000 &nbsp;//可能会受限1910&nbsp;</p><p>table_cache=512 &nbsp; &nbsp; &nbsp; &nbsp; //可能会受限 69（受限appserv，phpstudy不限）</p><pre class=\"brush:xml;toolbar:false\">mysql&gt;&nbsp;show&nbsp;variables&nbsp;like&nbsp;&#39;%max_connections%&#39;;\r\nmysql&gt;&nbsp;show&nbsp;variables&nbsp;like&nbsp;&#39;%table_cache%&#39;;\r\nmysql&gt;&nbsp;show&nbsp;global&nbsp;status&nbsp;like&nbsp;&#39;open%tables%&#39;;&nbsp;//(5.1.3之后这个值叫做table_open_cache)Open_tables：表示当前正在打开的表数目。</pre><p>Opened_tables：表示累计所有已经打开的表数目。</p><p>如果Open_tables的值已经接近table_cache的值，且Opened_tables还在不断变大，则说明mysql正在将缓存的表释放以容纳新的表，此时可能需要加大table_cache的值。</p><p>比较适合的值：</p><p>Open_tables / Opened_tables &gt;= 0.85</p><p>Open_tables / table_cache &lt;= 0.95</p><p>清空缓存：mysql &gt; flush tables;</p><p><br/></p>', '2018-10-31 08:16:00', '3', '1', '10000');
INSERT INTO `lei_daima` VALUES ('29', 'JQ滑动效果锚链接跳转', '<pre class=\"brush:js;toolbar:false\">function&nbsp;go_place(id){\r\n&nbsp;&nbsp;&nbsp;&nbsp;$(&quot;html,body&quot;).animate({scrollTop:($(&quot;#&quot;+id).offset().top-300)},1000);\r\n}</pre><p><br/></p>', '2018-11-19 09:04:56', '1', '1', '10000');
INSERT INTO `lei_daima` VALUES ('30', 'json_encode与json_decode', '<p style=\"border: 0px; margin: 2px 0px; padding: 0px; line-height: 1.8em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: left; text-indent: 0em;\"><strong>json_encode</strong></p><p>PHP json_encode() 用于对变量进行 JSON 编码，该函数如果执行成功返回 JSON 数据，否则返回 FALSE 。</p><pre class=\"brush:php;toolbar:false\">&lt;?php\r\n&nbsp;&nbsp;&nbsp;$arr&nbsp;=&nbsp;array(&#39;a&#39;&nbsp;=&gt;&nbsp;1,&nbsp;&#39;b&#39;&nbsp;=&gt;&nbsp;2,&nbsp;&#39;c&#39;&nbsp;=&gt;&nbsp;3,&nbsp;&#39;d&#39;&nbsp;=&gt;&nbsp;4,&nbsp;&#39;e&#39;&nbsp;=&gt;&nbsp;5);\r\n&nbsp;&nbsp;&nbsp;echo&nbsp;json_encode($arr);\r\n?&gt;</pre><p>以上代码执行结果为：</p><pre class=\"brush:php;toolbar:false\">{&quot;a&quot;:1,&quot;b&quot;:2,&quot;c&quot;:3,&quot;d&quot;:4,&quot;e&quot;:5}</pre><p>以下实例演示了如何将 PHP 对象转换为 JSON 格式数据：</p><pre class=\"brush:php;toolbar:false\">&lt;?php&nbsp;&nbsp;&nbsp;class&nbsp;Emp&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;$name&nbsp;=&nbsp;&quot;&quot;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;$hobbies&nbsp;&nbsp;=&nbsp;&quot;&quot;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;public&nbsp;$birthdate&nbsp;=&nbsp;&quot;&quot;;\r\n&nbsp;&nbsp;&nbsp;}\r\n&nbsp;&nbsp;&nbsp;$e&nbsp;=&nbsp;new&nbsp;Emp();\r\n&nbsp;&nbsp;&nbsp;$e-&gt;name&nbsp;=&nbsp;&quot;sachin&quot;;\r\n&nbsp;&nbsp;&nbsp;$e-&gt;hobbies&nbsp;&nbsp;=&nbsp;&quot;sports&quot;;\r\n&nbsp;&nbsp;&nbsp;$e-&gt;birthdate&nbsp;=&nbsp;date(&#39;m/d/Y&nbsp;h:i:s&nbsp;a&#39;,&nbsp;&quot;8/5/1974&nbsp;12:20:03&nbsp;p&quot;);\r\n&nbsp;&nbsp;&nbsp;$e-&gt;birthdate&nbsp;=&nbsp;date(&#39;m/d/Y&nbsp;h:i:s&nbsp;a&#39;,&nbsp;strtotime(&quot;8/5/1974&nbsp;12:20:03&quot;));\r\n&nbsp;&nbsp;&nbsp;echo&nbsp;json_encode($e);\r\n?&gt;</pre><p>以上代码执行结果为：</p><pre class=\"brush:php;toolbar:false\">{&quot;name&quot;:&quot;sachin&quot;,&quot;hobbies&quot;:&quot;sports&quot;,&quot;birthdate&quot;:&quot;08\\/05\\/1974&nbsp;12:20:03&nbsp;pm&quot;}</pre><p style=\"border: 0px; margin: 2px 0px; padding: 0px; line-height: 1.8em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: left; text-indent: 0em;\"><strong><br/></strong></p><p style=\"border: 0px; margin: 2px 0px; padding: 0px; line-height: 1.8em; color: rgb(51, 51, 51); white-space: normal; background-color: rgb(255, 255, 255); text-align: left; text-indent: 0em;\"><strong>json_decode</strong></p><p>PHP json_decode() 函数用于对 JSON 格式的字符串进行解码，并转换为 PHP 变量。</p><p>以下实例演示了如何解码 JSON 数据：</p><pre class=\"brush:php;toolbar:false\">&lt;?php\r\n&nbsp;&nbsp;&nbsp;$json&nbsp;=&nbsp;&#39;{&quot;a&quot;:1,&quot;b&quot;:2,&quot;c&quot;:3,&quot;d&quot;:4,&quot;e&quot;:5}&#39;;\r\n&nbsp;&nbsp;&nbsp;var_dump(json_decode($json));\r\n&nbsp;&nbsp;&nbsp;var_dump(json_decode($json,&nbsp;true));\r\n?&gt;</pre><p>以上代码执行结果为：</p><pre class=\"brush:php;toolbar:false\">object(stdClass)#1&nbsp;(5)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;[&quot;a&quot;]&nbsp;=&gt;&nbsp;int(1)\r\n&nbsp;&nbsp;&nbsp;&nbsp;[&quot;b&quot;]&nbsp;=&gt;&nbsp;int(2)\r\n&nbsp;&nbsp;&nbsp;&nbsp;[&quot;c&quot;]&nbsp;=&gt;&nbsp;int(3)\r\n&nbsp;&nbsp;&nbsp;&nbsp;[&quot;d&quot;]&nbsp;=&gt;&nbsp;int(4)\r\n&nbsp;&nbsp;&nbsp;&nbsp;[&quot;e&quot;]&nbsp;=&gt;&nbsp;int(5)}\r\n&nbsp;&nbsp;&nbsp;&nbsp;\r\narray(5)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;[&quot;a&quot;]&nbsp;=&gt;&nbsp;int(1)\r\n&nbsp;&nbsp;&nbsp;&nbsp;[&quot;b&quot;]&nbsp;=&gt;&nbsp;int(2)\r\n&nbsp;&nbsp;&nbsp;&nbsp;[&quot;c&quot;]&nbsp;=&gt;&nbsp;int(3)\r\n&nbsp;&nbsp;&nbsp;&nbsp;[&quot;d&quot;]&nbsp;=&gt;&nbsp;int(4)\r\n&nbsp;&nbsp;&nbsp;&nbsp;[&quot;e&quot;]&nbsp;=&gt;&nbsp;int(5)}</pre><p><br/></p>', '2018-11-28 13:46:46', '4', '1', '10000');
INSERT INTO `lei_daima` VALUES ('31', 'foreach循环方法使用', '<pre class=\"brush:php;toolbar:false\">$var_nandu_hack=array(&#39;|&#39;,&#39;,&#39;,&#39;#&#39;,&#39;*&#39;);\r\n\r\nforeach&nbsp;($var_nandu_hack&nbsp;as&nbsp;$hackval)&nbsp;{\r\n&nbsp;&nbsp;&nbsp;&nbsp;if&nbsp;(strpos($nandu_zhuanye,$hackval)!==false){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;alert_ini();\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;&quot;&lt;script&gt;alert_go(&#39;试题难度，存在禁用字符！&#39;,&#39;alert_back&#39;,&#39;&#39;,&#39;error&#39;,&#39;&#39;);&lt;/script&gt;&quot;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;exit;\r\n&nbsp;&nbsp;&nbsp;&nbsp;}\r\n}</pre><p><br/></p>', '2018-11-30 16:36:34', '4', '1', '10000');
INSERT INTO `lei_daima` VALUES ('32', 'JQ显示并在指定时间后隐藏', '<pre class=\"brush:js;toolbar:false\">$(function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$(&quot;#dianji&quot;).click(function(){\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$(&quot;#disappare&quot;).show().delay(3000).hide(300);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;});\r\n})</pre>', '2018-12-06 20:57:57', '1', '1', '50000');
INSERT INTO `lei_daima` VALUES ('33', 'PHP头header的常用方法', '<pre class=\"brush:php;toolbar:false\">&lt;?php\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;HTTP/1.1&nbsp;200&nbsp;OK&#39;);&nbsp;//&nbsp;ok&nbsp;正常访问\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;HTTP/1.1&nbsp;404&nbsp;Not&nbsp;Found&#39;);&nbsp;//通知浏览器&nbsp;页面不存在\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;HTTP/1.1&nbsp;301&nbsp;Moved&nbsp;Permanently&#39;);&nbsp;//设置地址被永久的重定向&nbsp;301\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Location:&nbsp;http://www.ithhc.cn/&#39;);&nbsp;//跳转到一个新的地址\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Refresh:&nbsp;10;&nbsp;url=http://www.ithhc.cn/&#39;);&nbsp;//延迟转向&nbsp;也就是隔几秒跳转\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;X-Powered-By:&nbsp;PHP/6.0.0&#39;);&nbsp;//修改&nbsp;X-Powered-By信息\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-language:&nbsp;en&#39;);&nbsp;//文档语言\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Length:&nbsp;1234&#39;);&nbsp;//设置内容长度\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Last-Modified:&nbsp;&#39;.gmdate(&#39;D,&nbsp;d&nbsp;M&nbsp;Y&nbsp;H:i:s&#39;,&nbsp;$time).&#39;&nbsp;GMT&#39;);&nbsp;//告诉浏览器最后一次修改时间\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;HTTP/1.1&nbsp;304&nbsp;Not&nbsp;Modified&#39;);&nbsp;//告诉浏览器文档内容没有发生改变\r\n&nbsp;&nbsp;&nbsp;&nbsp;###内容类型###\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Type:&nbsp;text/html;&nbsp;charset=utf-8&#39;);&nbsp;//网页编码\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Type:&nbsp;text/plain&#39;);&nbsp;//纯文本格式\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Type:&nbsp;image/jpeg&#39;);&nbsp;//JPG、JPEG&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Type:&nbsp;application/zip&#39;);&nbsp;//&nbsp;ZIP文件\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Type:&nbsp;application/pdf&#39;);&nbsp;//&nbsp;PDF文件\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Type:&nbsp;audio/mpeg&#39;);&nbsp;//&nbsp;音频文件&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-type:&nbsp;text/css&#39;);&nbsp;//css文件\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-type:&nbsp;text/javascript&#39;);&nbsp;//js文件\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-type:&nbsp;application/json&#39;);&nbsp;//json\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-type:&nbsp;application/pdf&#39;);&nbsp;//pdf\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-type:&nbsp;text/xml&#39;);&nbsp;//xml\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Type:&nbsp;application/x-shockw**e-flash&#39;);&nbsp;//Flash动画\r\n&nbsp;&nbsp;&nbsp;&nbsp;######\r\n&nbsp;&nbsp;&nbsp;&nbsp;###声明一个下载的文件###\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Type:&nbsp;application/octet-stream&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Disposition:&nbsp;attachment;&nbsp;filename=&quot;ITblog.zip&quot;&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Transfer-Encoding:&nbsp;binary&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;readfile(&#39;test.zip&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;######\r\n&nbsp;&nbsp;&nbsp;&nbsp;###对当前文档禁用缓存###\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Cache-Control:&nbsp;no-cache,&nbsp;no-store,&nbsp;max-age=0,&nbsp;must-revalidate&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Expires:&nbsp;Mon,&nbsp;26&nbsp;Jul&nbsp;1997&nbsp;05:00:00&nbsp;GMT&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;######\r\n&nbsp;&nbsp;&nbsp;&nbsp;###显示一个需要验证的登陆对话框###&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;HTTP/1.1&nbsp;401&nbsp;Unauthorized&#39;);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;WWW-Authenticate:&nbsp;Basic&nbsp;realm=&quot;Top&nbsp;Secret&quot;&#39;);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;######\r\n&nbsp;&nbsp;&nbsp;&nbsp;###声明一个需要下载的xls文件###\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Disposition:&nbsp;attachment;&nbsp;filename=ithhc.xlsx&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Type:&nbsp;application/vnd.openxmlformats-officedocument.spreadsheetml.sheet&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Length:&nbsp;&#39;.filesize(&#39;./test.xls&#39;));&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Content-Transfer-Encoding:&nbsp;binary&#39;);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Cache-Control:&nbsp;must-revalidate&#39;);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;header(&#39;Pragma:&nbsp;public&#39;);&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;readfile(&#39;./test.xls&#39;);&nbsp;\r\n?&gt;</pre><p><br/></p>', '2018-12-19 09:09:54', '4', '1', '60000');
INSERT INTO `lei_daima` VALUES ('34', 'Ajax检测网址是否有效', '<pre class=\"brush:js;toolbar:false\">&lt;script&nbsp;src=&quot;js/jquery.min.js&quot;&nbsp;type=&quot;text/javascript&quot;&nbsp;charset=&quot;gbk&quot;&nbsp;&gt;&lt;/script&gt;\r\n&lt;script&gt;\r\nfunction&nbsp;get_url_status(){\r\n	$.ajax({\r\n		url:&nbsp;&#39;22.php&#39;,\r\n		type:&nbsp;&#39;POST&#39;,\r\n		complete:&nbsp;function(response)&nbsp;{\r\n			if(response.status&nbsp;==&nbsp;200)&nbsp;{\r\n				alert(&#39;有效&#39;);\r\n			}&nbsp;else&nbsp;{\r\n				alert(&#39;无效&#39;);\r\n			}\r\n		}\r\n	});\r\n}\r\n&lt;/script&gt;\r\n\r\n&lt;input&nbsp;name=&quot;2&quot;&nbsp;type=&quot;button&quot;&nbsp;value=&quot;检测&quot;&nbsp;&nbsp;onclick=&quot;get_url_status()&quot;&nbsp;/&gt;</pre><p><br/></p>', '2019-06-17 14:36:46', '1', '1', '20000');
INSERT INTO `lei_daima` VALUES ('35', 'PHP获取传递的POST数据', '<pre class=\"brush:php;toolbar:false\">&lt;?\r\n&nbsp;&nbsp;&nbsp;&nbsp;//通过输入端来获取数据\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$body&nbsp;=&nbsp;file_get_contents(&#39;php://input&#39;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;&quot;---&nbsp;获取到的原始数据---&nbsp;&quot;.$body;\r\n&nbsp;&nbsp;&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;//解析数据\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$json&nbsp;=&nbsp;json_decode($body&nbsp;);\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;&quot;---&nbsp;解析数据---&quot;;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;&quot;name:&quot;.$json-&gt;{&#39;name&#39;};\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;echo&nbsp;&quot;age:&quot;.$json-&gt;{&#39;age&#39;};\r\n?&gt;</pre><p><br/></p>', '2019-07-01 09:16:01', '4', '1', '10000');
INSERT INTO `lei_daima` VALUES ('36', '小写数字转中文大写', '<pre class=\"brush:js;toolbar:false\">	function&nbsp;number_chinese()&nbsp;{\r\n			str=$(&quot;#jineall_xiaoxie&quot;).val()\r\n			var&nbsp;num&nbsp;=&nbsp;parseFloat(str);\r\n			var&nbsp;strOutput&nbsp;=&nbsp;&quot;&quot;,\r\n				strUnit&nbsp;=&nbsp;&#39;仟佰拾亿仟佰拾万仟佰拾元角分&#39;;\r\n			num&nbsp;+=&nbsp;&quot;00&quot;;\r\n			var&nbsp;intPos&nbsp;=&nbsp;num.indexOf(&#39;.&#39;);&nbsp;&nbsp;\r\n			if&nbsp;(intPos&nbsp;&gt;=&nbsp;0){\r\n				num&nbsp;=&nbsp;num.substring(0,&nbsp;intPos)&nbsp;+&nbsp;num.substr(intPos&nbsp;+&nbsp;1,&nbsp;2);\r\n			}\r\n			strUnit&nbsp;=&nbsp;strUnit.substr(strUnit.length&nbsp;-&nbsp;num.length);\r\n			for&nbsp;(var&nbsp;i=0;&nbsp;i&nbsp;&lt;&nbsp;num.length;&nbsp;i++){\r\n				strOutput&nbsp;+=&nbsp;&#39;零壹贰叁肆伍陆柒捌玖&#39;.substr(num.substr(i,1),1)&nbsp;+&nbsp;strUnit.substr(i,1);\r\n			}\r\n			$(&quot;#jineall_daxie&quot;).val(&nbsp;strOutput.replace(/零角零分$/,&nbsp;&#39;整&#39;).replace(/零[仟佰拾]/g,&nbsp;&#39;零&#39;).replace(/零{2,}/g,&nbsp;&#39;零&#39;).replace(/零([亿|万])/g,&nbsp;&#39;$1&#39;).replace(/零+元/,&nbsp;&#39;元&#39;).replace(/亿零{0,3}万/,&nbsp;&#39;亿&#39;).replace(/^元/,&nbsp;&quot;零元&quot;))\r\n\r\n	}</pre><p><br/></p>', '2019-07-16 08:38:21', '1', '1', '10000');
INSERT INTO `lei_daima` VALUES ('37', 'MYSQL自定义排序', '<pre class=\"brush:sql;toolbar:false\">select&nbsp;*&nbsp;from&nbsp;table&nbsp;order&nbsp;by&nbsp;field(pid,&#39;0&#39;,&#39;7&#39;,&#39;14&#39;,&#39;20&#39;,&#39;21&#39;),px</pre><p><br/></p>', '2019-08-18 10:24:49', '5', '1', '10000');
INSERT INTO `lei_daima` VALUES ('38', 'JS通过身份证获取出生日期和性别', '<pre class=\"brush:js;toolbar:false\">&lt;script&gt;\r\n//----------------------------------------------------------\r\n//&nbsp;&nbsp;&nbsp;&nbsp;功能：根据身份证号获得出生日期\r\n//&nbsp;&nbsp;参数：身份证号&nbsp;psidno\r\n//&nbsp;&nbsp;&nbsp;&nbsp;返回值：\r\n//&nbsp;&nbsp;&nbsp;&nbsp;出生日期\r\n//----------------------------------------------------------\r\n&nbsp;function&nbsp;GetBirthday(psidno){\r\n&nbsp;\r\n&nbsp;	leibie=$(&quot;#leibie&nbsp;option:selected&quot;).val();\r\n	\r\n	if&nbsp;(leibie==&#39;居民身份证&#39;){\r\n		var&nbsp;birthdayno,birthdaytemp\r\n		if(psidno.length==18){\r\n			birthdayno=psidno.substring(6,14)\r\n		}else&nbsp;if(psidno.length==15){\r\n			birthdaytemp=psidno.substring(6,12)\r\n			birthdayno=&quot;19&quot;+birthdaytemp\r\n		}else{\r\n			alert(&quot;错误的身份证号码，请核对！&quot;)\r\n			return&nbsp;false\r\n		}\r\n		var&nbsp;birthday=birthdayno.substring(0,4)+&quot;-&quot;+birthdayno.substring(4,6)+&quot;-&quot;+birthdayno.substring(6,8)\r\n		$(&quot;#riqi&quot;).val(birthday)\r\n	}\r\n}\r\n\r\n//----------------------------------------------------------\r\n//&nbsp;&nbsp;&nbsp;&nbsp;功能：根据身份证号获得性别\r\n//&nbsp;&nbsp;参数：身份证号&nbsp;psidno\r\n//&nbsp;&nbsp;&nbsp;&nbsp;返回值：\r\n//&nbsp;&nbsp;&nbsp;&nbsp;性别\r\n//----------------------------------------------------------\r\nfunction&nbsp;Getsex(psidno){\r\n\r\n	leibie=$(&quot;#leibie&nbsp;option:selected&quot;).val();\r\n	\r\n	if&nbsp;(leibie==&#39;居民身份证&#39;){\r\n		var&nbsp;sexno,sex\r\n		if(psidno.length==18){\r\n			sexno=psidno.substring(16,17)\r\n		}else&nbsp;if(psidno.length==15){\r\n			sexno=psidno.substring(14,15)\r\n		}else{\r\n			alert(&quot;错误的身份证号码，请核对！&quot;)\r\n			return&nbsp;false\r\n		}\r\n		var&nbsp;tempid=sexno%2;\r\n		if(tempid==0){\r\n			sex=&#39;女&#39;\r\n		}else{\r\n			sex=&#39;男&#39;\r\n		}\r\n	\r\n		$(&quot;:radio[class=&#39;xingbie&#39;][value=&#39;&quot;&nbsp;+&nbsp;sex&nbsp;+&nbsp;&quot;&#39;]&quot;).prop(&quot;checked&quot;,&nbsp;&quot;checked&quot;);\r\n	}\r\n}\r\n\r\n&lt;/script&gt;</pre><p><br/></p>', '2019-08-22 07:43:18', '1', '1', '10000');
INSERT INTO `lei_daima` VALUES ('39', 'JS回车模拟TAB换行', '<pre class=\"brush:js;toolbar:false\">&lt;script&gt;\r\n\r\n$(function()&nbsp;{\r\n	$(&quot;form[name=&#39;formsa&#39;]&nbsp;input:text&quot;).keypress(function(e)&nbsp;{\r\n		if&nbsp;(e.which&nbsp;==&nbsp;13){&nbsp;//&nbsp;判断所按是否回车键\r\n			var&nbsp;inputs&nbsp;=&nbsp;$(&quot;form[name=&#39;formsa&#39;]&quot;).find(&quot;:text&quot;);&nbsp;//&nbsp;获取表单中的所有输入框\r\n			var&nbsp;idx&nbsp;=&nbsp;inputs.index(this);&nbsp;//&nbsp;获取当前焦点输入框所处的位置\r\n			inputs[idx&nbsp;+&nbsp;1].focus();&nbsp;//&nbsp;设置焦点\r\n			inputs[idx&nbsp;+&nbsp;1].select();&nbsp;//&nbsp;选中文字\r\n			return&nbsp;false;\r\n		}\r\n	});\r\n});\r\n&lt;/script&gt;</pre><p><br/></p>', '2019-11-27 21:09:12', '1', '1', '10000');
INSERT INTO `lei_daima` VALUES ('40', 'mysql备份及rar打包相关关键配置代码', '<p>BAT（备份mysql及打包rar）</p><pre class=\"brush:xml;toolbar:false;\">set&nbsp;&quot;Y=%date:~,4%&quot;&nbsp;\r\nset&nbsp;&quot;m=%date:~5,2%&quot;&nbsp;\r\nset&nbsp;&quot;d=%date:~8,2%&quot;&nbsp;\r\n\r\nmd&nbsp;&quot;D:\\mysql_bak\\%Y%\\%m%&quot;\r\n\r\n&quot;D:\\AppServ\\MySQL\\bin\\mysqldump.exe&quot;&nbsp;&nbsp;--defaults-file=&quot;D:\\appserv\\mysql\\mysqldump-user.ini&quot;&nbsp;-uroot&nbsp;test&nbsp;&gt;&nbsp;D:\\mysql_bak\\%Y%\\%m%\\test_%Y%%m%%d%.sql\r\n\r\ncopy&nbsp;D:\\mysql_bak\\%Y%\\%m%\\test_%Y%%m%%d%.sql&nbsp;D:\\AppServ\\www\\auto_backup_bat\\mysql_test.sql\r\n\r\nstart&nbsp;C:\\&quot;Program&nbsp;Files&quot;\\WinRAR\\WinRAR.exe&nbsp;a&nbsp;D:\\AppServ\\www\\auto_backup_bat\\web_test.rar_bz&nbsp;D:\\AppServ\\www</pre><p>.HTACCESS（限制指定后缀文件，只能指定IP访问）</p><pre class=\"brush:xml;toolbar:false\">&lt;Files&nbsp;~&nbsp;&quot;.(sql|rar_bz)$&quot;&gt;\r\n&nbsp;&nbsp;&nbsp;&nbsp;order&nbsp;deny,allow\r\n&nbsp;&nbsp;&nbsp;&nbsp;deny&nbsp;from&nbsp;all\r\n&nbsp;&nbsp;&nbsp;&nbsp;allow&nbsp;from&nbsp;10.64.200.17\r\n&nbsp;&nbsp;&nbsp;&nbsp;allow&nbsp;from&nbsp;10.64.8.200\r\n&lt;/Files&gt;</pre><p>BAT（远程下载）</p><pre class=\"brush:xml;toolbar:false\">set&nbsp;&quot;Y=%date:~,4%&quot;&nbsp;\r\nset&nbsp;&quot;m=%date:~5,2%&quot;&nbsp;\r\nset&nbsp;&quot;d=%date:~8,2%&quot;&nbsp;\r\n\r\nmd&nbsp;&quot;F:\\Auto_download\\%Y%\\%m%\\%d%&quot;\r\n\r\npowershell&nbsp;(new-object&nbsp;System.Net.WebClient).DownloadFile(&#39;http://10.64.8.100/auto_backup_bat/mysql_test.sql&#39;,&#39;F:\\Auto_download\\%Y%\\%m%\\%d%\\test.sql&#39;)\r\n\r\npowershell&nbsp;(new-object&nbsp;System.Net.WebClient).DownloadFile(&#39;http://10.64.8.100/auto_backup_bat/web_test.rar_bz&#39;,&#39;F:\\Auto_download\\%Y%\\%m%\\%d%\\test.rar&#39;)</pre><p><br/></p>', '2019-12-13 08:59:22', '3', '1', '10000');
INSERT INTO `lei_daima` VALUES ('41', 'file_get_contents获取HTTPS内容', '<pre class=\"brush:php;toolbar:false\">&lt;?\r\n\r\nheader(&quot;Content-Type:&nbsp;text/html;charset=GB2312&quot;);\r\nsession_start();//开启session功能\r\ndate_default_timezone_set&nbsp;(&quot;PRC&quot;);//设置时区\r\nset_time_limit(0);\r\n\r\n$var_url&nbsp;=&nbsp;&#39;https://www.mdaima.com&#39;;\r\n\r\n$arrContextOptions&nbsp;=&nbsp;[\r\n&nbsp;&nbsp;&nbsp;&nbsp;&#39;ssl&#39;&nbsp;=&gt;&nbsp;[\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;verify_peer&#39;&nbsp;=&gt;&nbsp;false,\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&#39;verify_peer_name&#39;&nbsp;=&gt;&nbsp;false,\r\n&nbsp;&nbsp;&nbsp;&nbsp;]\r\n];\r\n$getv&nbsp;=&nbsp;file_get_contents($var_url,&nbsp;false,&nbsp;stream_context_create($arrContextOptions));\r\n\r\n\r\nif&nbsp;(&nbsp;strpos(&nbsp;$getv,&nbsp;&quot;-49.mp4&quot;&nbsp;)&nbsp;!==&nbsp;false&nbsp;)&nbsp;{\r\n	?&gt;\r\n	&lt;audio&nbsp;id=&quot;bgmp3&quot;&nbsp;autoplay&nbsp;loop&gt;\r\n		&lt;source&nbsp;src=&quot;yinsu_1.mp3&quot;&nbsp;type=&quot;audio/mpeg&quot;&gt;\r\n	&lt;/audio&gt;\r\n	&lt;?\r\n}else{\r\n	\r\n	echo&nbsp;date(&quot;Y-m-d&nbsp;H:i:s&quot;).&#39;-监控中......&#39;;\r\n}\r\n\r\n?&gt;</pre><p><br/></p>', '2020-02-28 16:12:23', '4', '1', '10000');

