-- ----------------------------
-- Table structure for `lei_jifen`
-- ----------------------------
DROP TABLE IF EXISTS `lei_jifen`;
CREATE TABLE `lei_jifen` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(50) NOT NULL,
  `indate` date NOT NULL,
  `intime` datetime NOT NULL,
  `jifen` int(11) NOT NULL,
  `leixing` varchar(30) NOT NULL,
  `ip` varchar(30) NOT NULL,
  `bz` varchar(100) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `leixing` (`leixing`),
  KEY `username` (`username`)
) ENGINE=MyISAM AUTO_INCREMENT=412 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of lei_jifen
-- ----------------------------

-- ----------------------------
-- Table structure for `lei_jingyan`
-- ----------------------------
DROP TABLE IF EXISTS `lei_jingyan`;
CREATE TABLE `lei_jingyan` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(100) NOT NULL,
  `title_code` varchar(500) NOT NULL,
  `shipin` varchar(35) NOT NULL,
  `zhiding` varchar(5) NOT NULL,
  `keyword` varchar(100) NOT NULL,
  `simgpaths` varchar(100) NOT NULL,
  `indate` datetime NOT NULL,
  `laiyuan` varchar(30) NOT NULL,
  `message` text NOT NULL,
  `hits` int(11) NOT NULL,
  `pass` varchar(5) NOT NULL,
  `huifuzt` varchar(5) NOT NULL,
  `pinglun` varchar(5) NOT NULL,
  `guanjianci` varchar(50) NOT NULL,
  PRIMARY KEY (`id`),
  KEY `pass` (`pass`),
  KEY `zhiding` (`zhiding`,`indate`),
  FULLTEXT KEY `title_code` (`title_code`)
) ENGINE=MyISAM AUTO_INCREMENT=73 DEFAULT CHARSET=utf8;

