DROP TABLE IF EXISTS `lei_user`;
CREATE TABLE `lei_user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `username` varchar(50) NOT NULL,
  `tel` varchar(15) NOT NULL,
  `userimg` varchar(150) NOT NULL,
  `password` varchar(50) NOT NULL,
  `nicheng` varchar(30) NOT NULL,
  `nicheng_py` varchar(20) NOT NULL,
  `jifen` varchar(30) NOT NULL,
  `jifen_all` varchar(30) NOT NULL COMMENT '历史积分总数',
  `pass` varchar(10) NOT NULL,
  `indate` datetime NOT NULL,
  `lasttime` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `username` (`username`)
) ENGINE=MyISAM AUTO_INCREMENT=219 DEFAULT CHARSET=utf8 COMMENT='会员表';

